import React, { useState, useEffect, Fragment } from 'react';
import '../data.css';

var check = true
var index = 0

function Data() {

    const [isData, setData] = useState([])
    const [isConfiguredData, setConfiguredData] = useState({
        headerNBA: [],
        headerNFL: [],
        headerNHL: [],
        headerMLB: [],
        headerSOCCER: [],
        imageNBA: [],
        imageNFL: [],
        imageNHL: [],
        imageMLB: [],
        imageSOCCER: [],
    })

    useEffect(() => {
        fetch('/api').then(
            res => res.json()
        ).then(data => setData(data))
    }, [])

    function setHeader(header, sport) {
        var tempHeader = []
        var count = 0
        var sHeader = String(header)
        let temp = ""
        check = true
        index = 0

        for (let x = 0; x < 10; x++) {
            check = true
            count = 0
            temp = ""
            while (check) {
                if (sHeader[index] == '`') {
                    check = false
                }
                if (count > 500) {
                    check = false
                }
                else {
                    temp += sHeader[index]
                    index++
                    count++
                }
            }
            let temp2 = temp.replace(/undefined/g, "")
            let temp3 = temp2.replace("`", "")
            if (sHeader[index - 2] != '.') {
                temp3 += "..."
            }
            else {
                temp3 += ".."
            }
            tempHeader.push(temp3)
        }
        if (sport === 0) {
            isConfiguredData.headerNBA = tempHeader
        }
        if (sport === 1) {
            isConfiguredData.headerNFL = tempHeader
        }
        if (sport === 2) {
            isConfiguredData.headerNHL = tempHeader
        }
        if (sport === 3) {
            isConfiguredData.headerMLB = tempHeader
        }
        if (sport === 4) {
            isConfiguredData.headerSOCCER = tempHeader
        }
        console.log("TEMP HEADER", isConfiguredData)
    }

    function setImage(image, sport) {
        var tempImage = []
        var count = 0
        var sImage = String(image)
        let temp = ""
        check = true
        index = 0

        for (let x = 0; x < 10; x++) {
            check = true
            count = 0
            temp = ""
            while (check) {
                if (sImage[index] == '`') {
                    check = false
                }
                if (count > 500) {
                    check = false
                }
                else {
                    temp += sImage[index]
                    index++
                    count++
                }
            }
            let temp2 = temp.replace(/undefined/g, "")
            let temp3 = temp2.replace("`", "")
            tempImage.push(temp3)
        }

        if (sport === 0) {
            isConfiguredData.imageNBA = tempImage
        }
        if (sport === 1) {
            isConfiguredData.imageNFL = tempImage
        }
        if (sport === 2) {
            isConfiguredData.imageNHL = tempImage
        }
        if (sport === 3) {
            isConfiguredData.imageMLB = tempImage
        }
        if (sport === 4) {
            isConfiguredData.imageSOCCER = tempImage
        }
    }

    return (
        <Fragment>
            {setHeader(isData.headerNBA, 0)}
            {setImage(isData.imageNBA, 0)}
            {isConfiguredData.headerNBA[0]}
            {isConfiguredData.imageNBA[0]}
        </Fragment>
    );
}

export default Data;
